sys_variable_value.value:
(function execute(inputs, outputs) {
  var stream = sn_fd.FlowAPI.executeDataStreamAction('sn_gpdisk_spoke.list_disks_by_zone', {
        "project_id": inputs.projectid,
        "zone": inputs.zone
    });
    var obj = []
    while (stream.hasNext()) {
        obj.push(stream.next())
    }
    var disks = [];
    obj.forEach(function(diskObj) {
        disks.push({
            "label": diskObj.name.toUpperCase(),
            "name": diskObj.name
        });
    })
    outputs.disks = {
        data: disks
    }
})(inputs, outputs);

